INSERT INTO `t_question_company` VALUES ('5169', '消防给水系统', '消防水泵控制柜防护等级（A类）', ' 消防水泵控制柜设置在专用消防水泵控制室时，其防护等级不应低于 IP30；与消防水泵设置 在同一空间时，其防护等级不应低于 IP55；', '《消防给水及消火栓系统技术规范 GB50974-2014》  11.0.9', 'sadas', 'A', '水泵房', '不合格', '2019-10-10 20:21:06', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5171', '消防给水系统', '消防水泵控制柜防淹防潮措施（C类）', '消防水泵控制柜应采取防止被水淹没的措施；在高温潮湿环境下，消防水泵控制柜内应设置自动防潮除湿的装置；', ' 《消防给水及消火栓系统技术规范 GB50974-2014》  11.0.10 ', null, 'C', '水泵房', null, '2019-10-10 20:21:06', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5172', '消防给水系统', '消防水泵控制柜安装要求（B类）', '消防水泵控制柜应符合 GB 16806 的有关规定。', '《消防联动控制系统 GB 16806-2006》', null, 'B', '水泵房', null, '2019-10-10 20:21:06', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5173', '消防给水系统', '消防水泵启动方式（A类）', '消防水泵应能手动启停和自动启动', '《消防给水及消火栓系统技术规范 GB50974-2014》  11.0.5', null, 'A', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5174', '消防给水系统', '消防水泵切换时间（B类）', '以备用电源切换方式或备用泵切换启动消防水泵时，消防水泵应分别在 1 min 或 2 min 内投入 正常运行', ' 《消防给水及消火栓系统技术规范 GB50974-2014》  13.1.4', null, 'B', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5175', '消防给水系统', '消防水泵的停泵（B类）', '消防水泵不应设置自动停泵的控制功能，停泵应由具有管理权限的工作人员根据火灾扑救情况确定。', '《消防给水及消火栓系统技术规范 GB50974-2014》  11.0.2', null, 'B', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5176', '消防给水系统', '柴油机消防水泵试验运行时间（B类）', '柴油机消防水泵应具备连续工作的性能，试验运行时间不应小于 24 h', '《消防给水及消火栓系统技术规范 GB50974-2014》  5.1.8', null, 'B', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5177', '消防给水系统', ' 柴油机消防水泵蓄电池（B类）', '柴油机消防水泵的蓄电池应保证消防水泵随时自动启泵的要求。', '《消防给水及消火栓系统技术规范 GB50974-2014》  5.1.8', null, 'B', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5178', '消防给水系统', '消防水泵控制柜设置（A类）', '消防水泵控制柜应设置机械应急启泵功能，并应保证在控制柜内的控制线路发生故障时由有管 理权限的人员在紧急时启动消防水泵；机械应急启动时，应确保消防水泵在报警后 5.0 min 内 正常工作。消防水泵控制柜前面板的明显部位应设置紧急时打开柜门的装置；', '《消防给水及消火栓系统技术规范 GB50974-2014》  11.0.12', null, 'A', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5179', '消防给水系统', '明敷时保护措施（A类项）', '明敷时，应穿金属导管或采用封闭式金属槽盒保护，金属导管或封闭式金属槽盒应采取防火保\n护措施；当采用阻燃或耐火电缆并敷设在电缆井、沟内时，可不穿金属导管或采用封闭式金属\n槽盒保护；当采用矿物绝缘类不燃性电缆时，可直接明敷；', '《建筑设计防火规范 GB 50016-2014》10.1.10.', null, 'A', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5180', '消防给水系统', '消防配电线路宜与其他配电线路敷设要求（A类项）', ' 消防配电线路宜与其他配电线路分开敷设在不同的电缆井、沟内；确有困难需敷设在同一电缆井、沟内时，应分别布置在电缆井、沟的两侧，且消防配电线路应采用矿物绝缘类不燃性电缆。', '《建筑设计防火规范 GB 50016-2014》10.1.10.3', null, 'A', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5181', '消防给水系统', '自动切换装置（B类项）', ' 一、二级负荷供电的消防控制室、消防水泵房、防烟与排烟风机房的消防用电设备及消防电梯等的供电，应在其配电线路的最末一级配电箱处设置自动切换装置。', '《建筑设计防火规范 GB 50016-2014》10.1.8', null, 'B', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5182', '消防给水系统', '手动控制方式的线路连接（A类）', '手动控制方式，应将消火栓泵控制箱（柜）的启动、停止按钮用专用线路直接连接至设置在消 防控制室内的消防联动控制器的手动控制盘，直接手动控制消火栓泵的启动、停止；', '《火灾自动报警系统设计规范 GB50116-2013》4.3.2A类', null, 'A', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5183', '消防给水系统', '消防水泵的选择（A类）', '消防水泵的选择和应用应符合下列规定： \n泵轴的密封方式和材料应满足消防水泵在低流量时运转的要求；', '《消防给水及消火栓系统技术规范 GB50974-2014》  5．1．6', null, 'A', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5184', '消防给水系统', '消防水泵（A类）', '消防水泵应采用自灌式吸水，并应在吸水管上设置检修阀门； ', '《消防给水及消火栓系统技术规范 GB50974-2014》 5.1.12.1A类', null, 'A', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5185', '消防给水系统', '消防水泵水泵驱动器（B类）', '消防水泵水泵驱动器宜采用电动机或柴油机直接传动，消防水泵不应采用双电动机或基于柴油 机等组成的双动力驱动水泵； ', '《消防给水及消火栓系统技术规范 GB50974-2014》 5.1.1 B类', null, 'B', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5186', '消防给水系统', '消防水泵的主要材质（B类）', '消防水泵的主要材质应符合下列规定：\n    1 水泵外壳宜为球墨铸铁；\n    2 叶轮宜为青铜或不锈钢。', '《消防给水及消火栓系统技术规范 GB50974-2014》 5．1．7 ', null, 'B', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5187', '消防给水系统', '防水泵系统名称和编号的标识（C类）', '消防水泵应有标明系统名称和编号的标识；', ' 《消防给水及消火栓系统技术规范 GB50974-2014》 14.0.12 C类', null, 'C', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5188', '消防给水系统', ' 消防水泵生产厂商（B类）', ' 消防水泵生产厂商应提供完整的水泵流量扬程性能曲线，并应标示流量、扬程、气蚀余量、功率和效率等参数。', ' 《消防给水及消火栓系统技术规范 GB50974-2014》  5．1．3', null, 'B', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5189', '消防给水系统', '一组消防水泵（B类）', '一组消防水泵应在消防水泵房内设置流量和压力测试装置，并应符合下列规定：\n1 单台消防水泵的流量不大于20L/s、设计工作压力不大于0.50MPa时，泵组应预留测量用流量计和压力计接口，其他泵组宜设置泵组流量和压力测试装置；\n2 消防水泵流量检测装置的计量精度应为0.4级，最大量程的75％应大于最大一台消防水泵设计流量值的175％；\n3 消防水泵压力检测装置的计量精度应为0.5级，最大量程的75％应大于最大一台消防水泵设计压力值的165％；\n4 每台消防水泵出水管上应设置DN65的试水管，并应采取排水措施。', ' 《消防给水及消火栓系统技术规范 GB50974-2014》 5．1．11 ', null, 'B', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5190', '消防给水系统', '单台消防水泵（C类）', '单台消防水泵的最小额定流量不应小于10L/s，最大额定流量不宜大于320L/s。', '《消防给水及消火栓系统技术规范 GB50974-2014》  5．1．4 ', null, 'C', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5191', '消防给水系统', '消防水泵机组（B类）', ' 消防水泵机组应由水泵、驱动器和专用控制柜等组成；一组消防水泵可由同一消防给水系统的工作泵和备用泵组成。', '《消防给水及消火栓系统技术规范 GB50974-2014》 5．1．2', null, 'B', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5192', '消防给水系统', '消防给水同一泵组的消防水泵（C类）', '消防给水同一泵组的消防水泵型号宜一致，且工作泵不宜超过3台；', '《消防给水及消火栓系统技术规范 GB50974-2014》 5.1.6.7 ', null, 'C', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5193', '消防给水系统', '多台消防水泵并联（B类）', '多台消防水泵并联时，应校核流量叠加对消防水泵出口压力的影响。', '《消防给水及消火栓系统技术规范 GB50974-2014》 5.1.6.8 ', null, 'B', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5194', '消防给水系统', '柴油机消防水泵（A类）', '采用柴油机消防水泵时应符合下列规定： \n1) 柴油机消防水泵应采用压缩式点火型柴油机； \n2) 柴油机的额定功率应校核海拔高度和环境温度对柴油机功率的影响； \n《消防给水及消火栓系统技术规范 GB50974-2014》  5.1.8(1、2) ', '《消防给水及消火栓系统技术规范 GB50974-2014》  5.1.8(1、2) ', null, 'A', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5195', '消防给水系统', '柴油机消防水泵（B类）', '3 柴油机消防水泵应具备连续工作的性能，试验运行时间不应小于24h；\n4 柴油机消防水泵的蓄电池应保证消防水泵随时自动启泵的要求；\n5 柴油机消防水泵的供油箱应根据火灾延续时间确定，且油箱最小有效容积应按1.5L/kw配置，柴油机消防水泵油箱内储存的燃料不应小于50％的储量', '《消防给水及消火栓系统技术规范 GB50974-2014》  5.1.8(3、4、5)', null, 'B', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5196', '消防给水系统', '轴流深井泵（A类）', '轴流深井泵宜安装于水井、消防水池和其他消防水源上，并应符合下列规定：\n1 轴流深井泵安装于水井时，其淹没深度应满足其可靠运行的要求，在水泵出流量为150％设计流量时，其最低淹没深度应是第一个水泵叶轮底部水位线以上不少于3.20m，且海拔高度每增加300m，深井泵的最低淹没深度应至少增加0.30m；\n2 轴流深井泵安装在消防水池等消防水源上时，其第一个水泵叶轮底部应低于消防水池的最低有效水位线，且淹没深度应根据水力条件经计算确定，并应满足消防水池等消防水源有效储水量或有效水位能全部被利用的要求；当水泵设计流量大于125L/s时，应根据水泵性能确定淹没深度，并应满足水泵气蚀余量的要求；\n3 轴流深井泵的出水管与消防给水管网连接应符合本规范第5．1．13条第3款的规定；\n4 轴流深井泵出水管的阀门设置应符合本规范第5．1．13条第5款和第6款的规定；\n5 当消防水池最低水位低于离心水泵出水管中心线或水源水位不能保证离心水泵吸水时，可采用轴流深井泵，并应采用湿式深坑的安装方式安装于消防水池等消防水源上；\n6 当轴流深井泵的电动机露天设置时，应有防雨功能；', '《消防给水及消火栓系统技术规范 GB50974-2014》 5.1.9（1、2、3、4、5、6）', null, 'A', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5197', '消防给水系统', '出口阀门（B类）', '进出口阀门应常开，且设有启闭标志；', ' 《消防给水及消火栓系统技术规范 GB50974-2014》 12.3.25.3', null, 'B', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5198', '消防给水系统', '消防水泵从市政管网直接抽水（A类）', '消防水泵从市政管网直接抽水时，应在消防水泵出水管上设置有空气隔断的倒流防止器；', ' 《消防给水及消火栓系统技术规范 GB50974-2014》 5.1.12.2', null, 'A', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5199', '消防给水系统', '吸水口处无吸水井（B类）', ' 当吸水口处无吸水井时，吸水口处应设置旋流防止器。', '《消防给水及消火栓系统技术规范 GB50974-2014》  5.1.12.3', null, 'B', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5200', '消防给水系统', '一组消防水泵的吸水管（A类）', '一组消防水泵，吸水管不应少于两条，当其中一条损坏或检修时，其余吸水管应仍能通过全部消防给水设计流量；', '《消防给水及消火栓系统技术规范 GB50974-2014》 5.1.13.1', null, 'A', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5201', '消防给水系统', '消防水泵吸水管布置（A类）', '消防水泵吸水管布置应避免形成气囊，吸水管水平管段变径连接时，应采用偏心异径管件并应 采用管顶平接；', '《消防给水及消火栓系统技术规范 GB50974-2014》 5.1.13.2', null, 'A', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5202', '消防给水系统', '一组消防水泵（A类）', '一组消防水泵应设不少于两条的输水干管与消防给水环状管网连接，当其中一条输水管检修时，其余输水管应仍能供应全部消防给水设计流量；', '《消防给水及消火栓系统技术规范 GB50974-2014》 5.1.13.3', null, 'A', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5203', '消防给水系统', '消防水泵吸水口的淹没深度、吸水管喇叭口、吸水管、出水管、吸水井口（B类）', ' 消防水泵吸水口的淹没深度应满足消防水泵在最低水位运行安全的要求，吸水管喇叭口在消防水池最低有效水位下的淹没深度应根据吸水管喇叭口的水流速度和水力条件确定，但不应小于600mm。当采用旋流防止器时，淹没深度不应小于200mm；\n    5 消防水泵的吸水管上应设置明杆闸阀或带自锁装置的蝶阀，但当设置暗杆阀门时应设有开启刻度和标志；当管径超过DN300时，宜设置电动阀门；\n    6 消防水泵的出水管上应设止回阀、明杆闸阀；当采用蝶阀时，应带有自锁装置；当管径大于DN300时，宜设置电动阀门；\n    7 消防水泵吸水管的直径小于DN250时，其流速宜为1.0m/s～1.2m/s；直径大于DN250时，宜为1.2m/s～1.6m/s；\n    8 消防水泵出水管的直径小于DN250时，其流速宜为1.5m/s～2.0m/s；直径大于DN250时，宜为2.0m/s～2.5m/s；\n    9 吸水井的布置应满足井内水流顺畅、流速均匀、不产生涡漩的要求，并应便于安装施工；\n    10 消防水泵的吸水管、出水管道穿越外墙时，应采用防水套管；当穿越墙体和楼板时，应符合本规范第12．3．19条第5款的要求；\n    11 消防水泵的吸水管穿越消防水池时，应采用柔性套管；采用刚性防水套管时应在水泵吸水管上设置柔性接头，且管径不应大于DN150。', '《消防给水及消火栓系统技术规范 GB50974-2014》 5.1.13（4、5、6、7、8、9、10、11）', null, 'B', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5204', '消防给水系统', '有两路消防供水且允许消防水泵直接吸水（B类）', '当有两路消防供水且允许消防水泵直接吸水时，应符合下列规定：\n1 每一路消防供水应满足消防给水设计流量和火灾时必须保证的其他用水；\n2 火灾时室外给水管网的压力从地面算起不应小于0.10MPa；\n3 消防水泵扬程应按室外给水管网的最低水压计算，并应以室外给水的最高水压校核消防水泵的工作工况。', '《消防给水及消火栓系统技术规范 GB50974-2014》 5．1．14 ', null, 'B', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5205', '消防给水系统', '消防水泵吸水管（B类）', ' 消防水泵吸水管可设置管道过滤器，管道过滤器的过水面积应大于管道过水面积的4倍，且孔径不宜小于3mm。B类', '《消防给水及消火栓系统技术规范 GB50974-2014》 5．1．15', null, 'B', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5206', '消防给水系统', '临时高压消防给水系统（B类）', '临时高压消防给水系统应采取防止消防水泵低流量空转过热的技术措施。', '《消防给水及消火栓系统技术规范 GB50974-2014》 5．1．16 ', null, 'B', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5207', '消防给水系统', ' 消防水泵吸水管和出水管（B类）', '消防水泵吸水管和出水管上应设置压力表，并应符合下列规定：\n1 消防水泵出水管压力表的最大量程不应低于其设计工作压力的2倍，且不应低于1.60MPa；\n2 消防水泵吸水管宜设置真空表、压力表或真空压力表，压力表的最大量程应根据工程具体情况确定，但不应低于0.70MPa，真空表的最大量程宜为－0.10MPa；\n3 压力表的直径不应小于100mm，应采用直径不小于6mm的管道与消防水泵进出口管相接，并应设置关断阀门。', '《消防给水及消火栓系统技术规范 GB50974-2014》  5．1．17 ', null, 'B', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5208', '消防给水系统', '水泵出水管（B类）', '水泵出水管上应设置试验和检查用的压力表、放水阀；当存在超压可能时，出水管上应设置防超压设施；', '《消防给水及消火栓系统技术规范 GB50974-2014》 12.3.2', null, 'B', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5209', '消防给水系统', '竖管数量及竖管阀门（B）', 'd) 室内消火栓竖管应保证检修管道时关闭停用的竖管不超过1根，当竖管超过4根时，可关闭不相邻的2根；每根竖管与供水横干管相接处应设置阀门； ', '《消防给水及消火栓系统技术规范 GB50974-2014》8.1.6', null, 'B', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5210', '消防给水系统', '室内消给水管网与自动喷水等其他水灭火系统的管网之间的设置（B类）', '(e) 室内消火栓给水管网宜与自动喷水等其他水灭火系统的管网分开设置；当合用消防泵时，供水管路沿水流方向应在报警阀前分开设置；', '《消防给水及消火栓系统技术规范 GB50974-2014》8.1.7', null, 'B', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5211', '消防给水系统', '消防水泵出水管上的止回阀（C类）', ' 消防水泵出水管上的止回阀宜采用水锤消除止回阀，当消防水泵供水高度超过24m时，应采用水锤消除器。当消防水泵出水管上设有囊式气压水罐时，可不设水锤消除设施。', ' 《消防给水及消火栓系统技术规范 GB50974-2014》8．3．3', null, 'C', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5212', '消防给水系统', '室内消防给水系统由生活、生产给水系统管网直接供水时的设置（A类）', '(g) 室内消防给水系统由生活、生产给水系统管网直接供水时，应在引入管处设置倒流防止器；当消防给水系统采用有空气隔断的倒流防止器时，该倒流防止器应设置在清洁卫生的场所，其排水口应采取防止被水淹没的技术措施；', '《消防给水及消火栓系统技术规范 GB50974-2014》8.3.5', null, 'A', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5213', '消防给水系统', ' 消防给水系统的阀门选择（B类）', '消防给水系统的阀门选择应符合下列规定：\n1 埋地管道的阀门宜采用带启闭刻度的暗杆闸阀，当设置在阀门井内时可采用耐腐蚀的明杆闸阀；2 室内架空管道的阀门宜采用蝶阀、明杆闸阀或带启闭刻度的暗杆闸阀等；3 室外架空管道宜采用带启闭刻度的暗杆闸阀或耐腐蚀的明杆闸阀；4 埋地管道的阀门应采用球墨铸铁阀门，室内架空管道的阀门应采用球墨铸铁或不锈钢阀门，室外架空管道的阀门应采用球墨铸铁阀门或不锈钢阀门。', '《消防给水及消火栓系统技术规范 GB50974-2014》 8．3．1 ', null, 'B', '水泵房', null, '2019-10-10 20:21:07', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5214', '消防给水系统', '应采取消防排水措施的建筑物（B类）', ' 下列建筑物和场所应采取消防排水措施：\n    1 消防水泵房；\n    2 设有消防给水系统的地下室； \n    3 消防电梯的井底；\n    4 仓库。', '《消防给水及消火栓系统技术规范 GB50974-2014》9．2．1', null, 'B', '水泵房', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5215', '消防给水系统', '室内消防排水规定（B类）', '室内消防排水应符合下列规定：\n    1 室内消防排水宜排入室外雨水管道；\n    2 当存有少量可燃液体时，排水管道应设置水封，并宜间接排入室外污水管道；\n    3 地下室的消防排水设施宜与地下室其他地面废水排水设施共用。', '《消防给水及消火栓系统技术规范 GB50974-2014》9．2．2 ', null, 'B', '水泵房', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5216', '消防给水系统', '消防水泵房起重设施（C类）', 'c) 消防水泵房应按照相关规范要求设置起重设施；\n1 消防水泵的重量小于0.5t时，宜设置固定吊钩或移动吊架；\n2 消防水泵的重量为0.5t～3t时，宜设置手动起重设备；\n3 消防水泵的重量大于3t时，应设置电动起重设备。', '《消防给水及消火栓系统技术规范 GB50974-2014》5.5.1（1、2、3）', null, 'C', '水泵房', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5217', '消防给水系统', '消防水泵机组的布置（C类）', '5．5．2 消防水泵机组的布置应符合下列规定：\n1 相邻两个机组及机组至墙壁间的净距，当电机容量小于22kW时，不宜小于0.60m；当电动机容量不小于22kW，且不大于55kW时，不宜小于0.8m；当电动机容量大于55kW且小于255kW时，不宜小于1.2m；当电动机容量大于255kW时，不宜小于1.5m；\n2 当消防水泵就地检修时，应至少在每个机组一侧设消防水泵机组宽度加0.5m的通道，并应保证消防水泵轴和电动机转子在检修时能拆卸；\n3 消防水泵房的主要通道宽度不应小于1.2m。', '《消防给水及消火栓系统技术规范 GB50974-2014》 5.5.2（1、2、3）', null, 'C', '水泵房', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5218', '消防给水系统', '采用柴油机消防水泵时，机组间的净距（C类）', '当采用柴油机消防水泵时，机组间的净距宜按本规范第5．5．2条规定值增加0.2m，但不应小于1.2m。', '《消防给水及消火栓系统技术规范 GB50974-2014》5．5．3 ', null, 'C', '水泵房', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5219', '消防给水系统', '消防水泵房集中检修场地（B类）', '当消防水泵房内设有集中检修场地时，其面积应根据水泵或电动机外形尺寸确定，并应在周围留有宽度不小于0.7m的通道。地下式泵房宜利用空间设集中检修场地。对于装有深井水泵的湿式竖井泵房，还应设堆放泵管的场地。', '《消防给水及消火栓系统技术规范 GB50974-2014》 5．5．4 ', null, 'B', '水泵房', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5220', '消防给水系统', '消防水泵房内的架空水管道（C类）', 'b) 消防水泵房内的架空水管道，不应阻碍通道和跨越电气设备，当必须跨越时，应采取保证通道畅通和保护电气设备的措施；', ' 《消防给水及消火栓系统技术规范 GB50974-2014》   5.5.5', null, 'C', '水泵房', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5221', '消防给水系统', '消防水泵房地面层的地坪至屋盖或天花板等的突出构件底部间的净高要求（B类）', ' 独立的消防水泵房地面层的地坪至屋盖或天花板等的突出构件底部间的净高，除应按通风采光等条件确定外，且应符合下列规定：\n1 当采用固定吊钩或移动吊架时，其值不应小于3.0m；\n2 当采用单轨起重机时，应保持吊起物底部与吊运所越过物体顶部之间有0.50m以上的净距；\n3 当采用桁架式起重机时，除应符合本条第2款的规定外，还应另外增加起重机安装和检修空间的高度。', ' 《消防给水及消火栓系统技术规范 GB50974-2014》 5．5．6', null, 'B', '水泵房', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5222', '消防给水系统', '采用轴流深井水泵时，水泵房净高要求（B类）', '当采用轴流深井水泵时，水泵房净高应按消防水泵吊装和维修的要求确定，当高度过高时，应根据水泵传动轴长度产品规格选择较短规格的产品。', ' 《消防给水及消火栓系统技术规范 GB50974-2014》 5．5．7 ', null, 'B', '水泵房', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5223', '消防给水系统', '消防水泵房的门（B类）', '消防水泵房应至少有一个可以搬运最大设备的门。 ', ' 《消防给水及消火栓系统技术规范 GB50974-2014》 5．5．8 ', null, 'B', '水泵房', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5224', '消防给水系统', '消防水泵房采暖温度（A类）', '严寒、寒冷等冬季结冰地区采暖温度不应低于 10 ℃，但当无人值守时不应低于 5 ℃。', ' 《消防给水及消火栓系统技术规范 GB50974-2014》   5.5.9.1 A类', null, 'A', '水泵房', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5225', '消防给水系统', '消防水泵房的通风（C类）', '消防水泵房的通风宜按6次/h设计；', ' 《消防给水及消火栓系统技术规范 GB50974-2014》 5.5.9.2 ', null, 'C', '水泵房', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5226', '消防给水系统', '消防水泵位置的环境要求（B类）', '消防水泵不宜设在有防振或有安静要求房间的上一层、下一层和毗邻位置，当必须时，应采取下列降噪减振措施：\n1 消防水泵应采用低噪声水泵；\n2 消防水泵机组应设隔振装置；\n3 消防水泵吸水管和出水管上应设隔振装置；\n4 消防水泵房内管道支架和管道穿墙和穿楼板处，应采取防止固体传声的措施；\n5 在消防水泵房内墙应采取隔声吸音的技术措施。', ' 《消防给水及消火栓系统技术规范 GB50974-2014》 5．5．10 ', null, 'B', '水泵房', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5227', '消防给水系统', ' 消防水泵房应符合的规定（A类）', 'a) 消防水泵房应符合下列规定： \n1) 独立建造的消防水泵房耐火等级不应低于二级； \n2) 附设在建筑物内的消防水泵房，不应设置在地下三层及以下，或室内地面与室外出入口地 坪高差大于 10 m 的地下楼层； \n3) 附设在建筑物内的消防水泵房，其疏散门应直通安全出口，且开向疏散走道的门应采用甲 级防火门。', ' 《消防给水及消火栓系统技术规范 GB50974-2014》   5.5.12 A类', null, 'A', '水泵房', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5228', '消防给水系统', '有柴油机消防水泵的消防水泵房（C类）', 'd) 当采用柴油机消防水泵时宜设置独立消防水泵房，并应设置满足柴油机运行的通风、排烟和阻火设施；', ' 《消防给水及消火栓系统技术规范 GB50974-2014》   5.5.13C类', null, 'C', '水泵房', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5229', '消防给水系统', '消防水泵房排水及防水淹措施（B类）', 'e) 消防水泵房应设置排水及采取防水淹没的技术措施；', '《消防给水及消火栓系统技术规范 GB50974-2014》   5.5.14 B类', null, 'B', '水泵房', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5230', '消防给水系统', '减压阀的设置（B类）', '(i) 减压阀的设置应符合设计要求及 4.3.2.1 d)条的要求。', '《消防给水及消火栓系统技术规范 GB50974-2014》  5.2.5 B类', null, 'B', '水泵房', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5231', '消防给水系统', '减压阀设置(B 类项)', '配水管道的工作压力不应大于 1.20MPa，', '《自动喷水灭火系统施工及验收规范 GB50261-2017》8.0.1', null, 'B', '水泵房', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5232', '消防给水系统', ' 减压阀的设置（B类）', '减压阀的设置应符合下列规定：\n1 减压阀应设置在报警阀组入口前，当连接两个及以上报警阀组时，应设置备用减压阀；\n2 减压阀的进口处应设置过滤器，过滤器的孔网直径不宜小于4目/cm2～5目/cm2，过流面积不应小于管道截面积的4倍；\n3 过滤器和减压阀前后应设压力表，压力表的表盘直径不应小于100mm，最大量程宜为设计压力的2倍；\n4 过滤器前和减压阀后应设置控制阀门；\n5 减压阀后应设置压力试验排水阀；\n6 减压阀应设置流量检测测试接口或流量计；\n7 垂直安装的减压阀，水流方向宜向下；\n8 比例式减压阀宜垂直安装，可调式减压阀宜水平安装；\n9 减压阀和控制阀门宜有保护或锁定调节配件的装置；\n10 接减压阀的管段不应有气堵、气阻。', '《消防给水及消火栓系统技术规范 GB50974-2014》 8．3．4 ', null, 'B', '水泵房', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5233', '消防给水系统', '离心泵（C类）', '稳压泵宜采用离心泵，并宜符合下列规定：\n1 宜采用单吸单级或单吸多级离心泵；\n2 泵外壳和叶轮等主要部件的材质宜采用不锈钢。', '《消防给水及消火栓系统技术规范 GB50974-2014》 5．3．1 ', null, 'C', '水泵房', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5234', '消防给水系统', '设置稳压泵的临时高压消防给水系统（B类）', 'b) 设置稳压泵的临时高压消防给水系统应设置防止稳压泵频繁启停的技术措施，当采用气压水罐时其有效储水容积不宜小于 150 L；', '《消防给水及消火栓系统技术规范 GB50974-2014》  5.3.4', null, 'B', '水泵房', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5235', '消防给水系统', '稳压泵吸水管、出水管（B类）', 'c) 稳压泵吸水管应设置明杆闸阀，出水管应设置消声止回阀和明杆闸阀；', '《消防给水及消火栓系统技术规范 GB50974-2014》  5.3.5', null, 'B', '水泵房', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5236', '消防给水系统', '备用泵（B类）', 'd) 稳压泵应设置备用泵；', '《消防给水及消火栓系统技术规范 GB50974-2014》  5.3.6', null, 'B', '水泵房', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5237', '消防给水系统', '消防水池有效容积（A类）', '消防水池有效容积应满足火灾延续时间内所需消防用水量，当消防水池采用两路消防供水且在 火灾情况下连续补水能满足消防要求时，消防水池的有效容积应满足设计要求，但不应小于 100 m3；当仅设有消火栓系统时不应小于 50 m3；', '《消防给水及消火栓系统技术规范 GB50974-2014》4.3.4', null, 'A', '消防水源', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5238', '消防给水系统', '高位消防水池（A类）', ' 1 高位消防水池的有效容积、出水、排水和水位，应符合本规范第4．3．8条和第4．3．9条的规定；\n 2 高位消防水池的通气管和呼吸管等应符合本规范第4．3．10条的规定；\n 3 除可一路消防供水的建筑物外，向高位消防水池供水的给水管不应少于两条；\n 4 当高层民用建筑采用高位消防水池供水的高压消防给水系统时，高位消防水池储存室内消防用水量确有困难，但火灾时补水可靠，其总有效容积不应小于室内消防用水量的50％；', '《消防给水及消火栓系统技术规范 GB50974-2014》4.3.11', null, 'A', '消防水源', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5239', '消防给水系统', '保证消防给水系统所需的水量和水质的技术措施（A类)', '雨水清水池、中水清水池、水景和游泳池必须作为消防水源时，应有保证在任何情况下均能满足消防给水系统所需的水量和水质的技术措施。', '《消防给水及消火栓系统技术规范 GB50974-2014》4.1.6', null, 'A', '消防水源', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5240', '消防给水系统', '消防用水与其他用水共用的水池（A类）', '消防用水与其他用水共用的水池，应采取确保消防用水量不作他用的技术措施；', '《消防给水及消火栓系统技术规范 GB50974-2014》4.3.8', null, 'A', '消防水源', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5241', '消防给水系统', '消防水池、水塔和高位消防水池等防冻措施（A类）', '严寒、寒冷等冬季结冰地区的消防水池、水塔和高位消防水池等应采取防冻措施；', '《消防给水及消火栓系统技术规范 GB50974-2014》4.1.5', null, 'A', '消防水源', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5242', '消防给水系统', '消防给水管道内平时所充水的pH值（C类）', '消防给水管道内平时所充水的 pH 值应为 6.0～9.0；', '《消防给水及消火栓系统技术规范 GB50974-2014》4.1.4', null, 'C', '消防水源', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5243', '消防给水系统', '消防水池进水管径（B类）', '消防水池进水管管径应符合设计要求，且不应小于 DN100；', '《消防给水及消火栓系统技术规范 GB50974-2014》4.3.3', null, 'B', '消防水源', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5244', '消防给水系统', '消防水池、出水管及连通管（C 类）', '消防水池的总蓄水有效容积大于 500 m3时，宜设两格能独立使用的消防水池；当大于 1000 m3 时，应设置能独立使用的两座消防水池；每格(或座)消防水池应设置独立的出水管，并应设置满足低有效水位的连通管；', '《消防给水及消火栓系统技术规范 GB50974-2014》4.3.6', null, 'C', '消防水源', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5245', '消防给水系统', '消防水池、 高位消防水池的出水、排水和水位（A类）', '消防水池、 高位消防水池的出水、排水和水位应符合下列规定：\n1) 消防水池的出水管应保证消防水池的有效容积能被全部利用； \n2) 应设置就地水位显示装置，并应在消防控制室或值班室等地点设置显示消防水池水位的装 置，同时应有高和低报警水位； \n3) 应设置溢流水管和排水设施，并应采用间接排水。', '《消防给水及消火栓系统技术规范 GB50974-2014》9.3.1《消防给水及消火栓系统技术规范 GB50974-2014》4.3.9A类', null, 'A', '消防水源', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5246', '消防给水系统', '储存室外消防用水的消防水池或供消防车取水的消防水池（B类）', '储存室外消防用水的消防水池或供消防车取水的消防水池，应符合下列规定： 1) 消防水池应设置取水口(井)，且吸水高度不应大于 6.0 m； 2) 取水口(井)与建筑物(水泵房除外)的距离不宜小于 15 m； 3) 取水口(井)与甲、乙、丙类液体储罐等构筑物的距离不宜小于 40 m。', '《消防给水及消火栓系统技术规范 GB50974-2014》4.3.7', null, 'B', '消防水源', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5247', '消防给水系统', '取水口(井)（B类）', '取水口(井)与液化石油气储罐的距离不宜小于 60 m，当采取防止辐射热保护措施时，可为 40 m；', '《消防给水及消火栓系统技术规范 GB50974-2014》4.3.7', null, 'B', '消防水源', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5248', '消防给水系统', '消防水池、高位消防水池通气管（C类）', '消防水池、高位消防水池应设置通气管；消防水池通气管、呼吸管和溢流水管等应采取防止虫 鼠等进入消防水池的技术措施；', '《消防给水及消火栓系统技术规范 GB50974-2014》3.10', null, 'C', '消防水源', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5249', '消防给水系统', '高层民用建筑高压消防给水系统（B类）', '1) 高层民用建筑高压消防给水系统的高位消防水池总有效容积大于 200 m3时，宜设置蓄水有效容积相等且可独立使用的两格；当建筑高度大于 100 m 时应设置独立的两座；每格或座应有一条独立的出水管向消防给水系统供水；\n 2) 高位消防水池设置在建筑物内时，应设甲级防火门；且消防水池及其支承框架与建筑构件 应连接牢固。', '《消防给水及消火栓系统技术规范 GB50974-2014》4.3.11', null, 'B', '消防水源', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5250', '消防给水系统', '采用天然水源当室外消防水源（A类）', '当室外消防水源采用天然水源时，应采取防止冰凌、漂浮物、悬浮物等物质堵塞消防水泵的技术措施，并应采取确保安全取水的措施； ', '《消防给水及消火栓系统技术规范 GB50974-2014》4.4.4', null, 'A', '消防水源', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5251', '消防给水系统', '地表水作为室外消防水源', '当地表水作为室外消防水源时，应采取确保消防车、固定和移动消防水泵在枯水位取水的技术 措施；当消防车取水时，大吸水高度不应超过 6.0 m；', '《消防给水及消火栓系统技术规范 GB50974-2014》4.4.5', null, null, '消防水源', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5252', '消防给水系统', '水井不利水位（A类）', '水井不利水位应满足水泵吸水要求，并应设置探测水井水位的水位测试装置；', '《消防给水及消火栓系统技术规范 GB50974-2014》4.4.5', null, 'A', '消防水源', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5253', '消防给水系统', '设有消防车取水口的天然水源（A类）', '设有消防车取水口的天然水源，应设置消防车到达取水口的消防车道和消防车回车场或回车道；', ' 《消防给水及消火栓系统技术规范 GB50974-2014》4.4.7', null, 'A', '消防水源', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5254', '消防给水系统', '天然水源消防车取水口（C类）', '天然水源消防车取水口的设置位置和设施，应符合 GB 50013 等国家相关技术标准的规定，且取水头部宜设置格栅，其栅条间距不宜小于 50 mm，也可采用过滤管。', '《消防给水及消火栓系统技术规范 GB50974-2014》4.4.6', null, 'C', '消防水源', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5255', '消防给水系统', '减压阀的设置（B类）', '减压阀的设置应符合设计要求及 4.3.2.1 d)条的要求。', '《消防给水及消火栓系统技术规范 GB50974-2014》5.2.5', null, 'B', '消防水源', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5256', '消防给水系统', '减压阀处的压力试验排水管道直径确定及消防水池、 高位消防水池的出水、排水和水位（A类）', '减压阀处的压力试验排水管道直径应根据减压阀流量确定，但不应小于 DN100；消防水池、 高位消防水池的出水、排水和水位应符合下列规定：\n1) 消防水池的出水管应保证消防水池的有效容积能被全部利用； \n2) 应设置就地水位显示装置，并应在消防控制室或值班室等地点设置显示消防水池水位的装 置，同时应有高和低报警水位； \n3) 应设置溢流水管和排水设施，并应采用间接排水。', '《消防给水及消火栓系统技术规范 GB50974-2014》9.3.1《消防给水及消火栓系统技术规范 GB50974-2014》4.3.9', null, 'A', '消防水源', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5257', '消防给水系统', '减压阀设置(B 类项)', '配水管道的工作压力不应大于 1.20MPa，', '《自动喷水灭火系统设计规范 GB50084-2017》8.0.1', null, 'B', '消防水源', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5258', '消防给水系统', '减压阀的设置（B类）', '减压阀的设置应符合下列规定：\n1 减压阀应设置在报警阀组入口前，当连接两个及以上报警阀组时，应设置备用减压阀；\n2 减压阀的进口处应设置过滤器，过滤器的孔网直径不宜小于4目/cm2～5目/cm2，过流面积不应小于管道截面积的4倍；\n3 过滤器和减压阀前后应设压力表，压力表的表盘直径不应小于100mm，最大量程宜为设计压力的2倍；\n4 过滤器前和减压阀后应设置控制阀门；\n5 减压阀后应设置压力试验排水阀；\n6 减压阀应设置流量检测测试接口或流量计；\n7 垂直安装的减压阀，水流方向宜向下；\n8 比例式减压阀宜垂直安装，可调式减压阀宜水平安装；\n9 减压阀和控制阀门宜有保护或锁定调节配件的装置；\n10 接减压阀的管段不应有气堵、气阻。', '《消防给水及消火栓系统技术规范 GB50974-2014》8．3．4', null, 'B', '消防水源', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5259', '消防给水系统', '减压阀处的压力试验排水管道直径确定及消防水池、 高位消防水池的出水、排水和水位（A类）', 'h) 减压阀处的压力试验排水管道直径应根据减压阀流量确定，但不应小于 DN100；消防水池、 高位消防水池的出水、排水和水位应符合下列规定：\n1) 消防水池的出水管应保证消防水池的有效容积能被全部利用； \n2) 应设置就地水位显示装置，并应在消防控制室或值班室等地点设置显示消防水池水位的装 置，同时应有高和低报警水位； \n3) 应设置溢流水管和排水设施，并应采用间接排水。', '《消防给水及消火栓系统技术规范 GB50974-2014》9.3.1《消防给水及消火栓系统技术规范 GB50974-2014》4.3.9', null, 'A', '消防水源', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5260', '消防给水系统', '串联转输水箱的溢流管（B类）', '串联转输水箱的溢流管宜连接到消防水池；', 'GB 50974  6.2.3.2 B类', null, 'B', '消防水源', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5261', '消防给水系统', '采用消防水泵直接串联的要求（B类）', '当采用消防水泵直接串联时，应采取确保供水可靠性的措施，并应在串联消防水泵出水管上设置减压型倒流防止器；', '《消防给水及消火栓系统技术规范 GB50974-2014》6.2.3.4', null, 'B', '消防水源', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5262', '消防给水系统', '采用减压阀减压分区供水时减压阀的安装（B类）', '采用减压阀减压分区供水时，减压阀的安装位置力应符合设计要求，每一供水分区应设不少于两组减压阀组，每组减压阀组宜设置备用减压阀；减压阀仅应设置在单向流动的供水管上，当一级减压阀减压不能满足要求时，可采用减压阀串联减压，但串联减压不应大于两级，第二级减压阀宜采用先导式减压阀。', '《消防给水及消火栓系统技术规范 GB50974-2014》6.2.4', null, 'B', '消防水源', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5263', '消防给水系统', '采用减压水箱减压分区供水时，减压水箱的安装（A类）', '采用减压水箱减压分区供水时：减压水箱的设置应符合高位水箱的技术要求，且有效容积不应 小于 18 m3，宜分为两格。', '《消防给水及消火栓系统技术规范 GB50974-2014》6.2.5.3', null, 'A', '消防水源', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5264', '消防给水系统', '消防水泵转输水箱串联（B类）', '当采用消防水泵转输水箱串联时，应符合：转输水箱的有效储水容积不应小于 60 m3', '《消防给水及消火栓系统技术规范 GB50974-2014》6.2.3', null, 'B', '消防水源', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5265', '消防给水系统', '消防水泵直接串联（B类）', '当采用消防水泵直接串联时，消防水泵从低区到高区应能依次顺序启动，应校核系统供水压力', '《消防给水及消火栓系统技术规范 GB50974-2014》6.2.3  3', null, 'B', '消防水源', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5266', '消防给水系统', '减压阀减压分区供水（B类）', '采用减压阀减压分区供水时:减压阀前、后的压力应符合设计要求；减压阀的阀前阀后压力比值不宜大于 3:1，阀前后压力差不宜超过 0.40 MPa；\n减压阀后应设置安全阀，安全阀的开启压力应能满足系统安全，且不应影响系统的供水安全性；', '《消防给水及消火栓系统技术规范 GB50974-2014》6.2.4', null, 'B', '消防水源', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5267', '消防给水系统', '消防水池、出水管及连通管（C 类）', 'd) 消防水池的总蓄水有效容积大于 500 m3时，宜设两格能独立使用的消防水池；当大于 1000 m3 时，应设置能独立使用的两座消防水池；每格(或座)消防水池应设置独立的出水管，并应设置满足低有效水位的连通管；', '《消防给水及消火栓系统技术规范 GB50974-2014》4.3.6', null, 'C', '资料', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5268', '消防给水系统', '消防水池进水管径（B类）', 'b) 消防水池进水管管径应符合设计要求，且不应小于 DN100；', '《消防给水及消火栓系统技术规范 GB50974-2014》4.3.3', null, 'B', '资料', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5269', '消防给水系统', '进水管、溢流管、喇叭口、出水管管径（B类）', 'g) 进水管管径应满足设计要求，但管径不应小于 DN32，进水管宜设置液位阀或浮球阀；溢流管的直径不应小于进水管直径的 2 倍，且不应小于 DN100，喇叭口直径不应小于溢流管直径的 （1.5～2.5）倍；高位消防水箱出水管管径应满足消防给水设计流量的出水要求，且不应小于 DN100；', '《消防给水及消火栓系统技术规范 GB50974-2014》5.2.6   (5 8 9 ）', null, 'B', '资料', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5270', '消防给水系统', '采用轴流深井水泵时，水泵房净高要求（B类）', '当采用轴流深井水泵时，水泵房净高应按消防水泵吊装和维修的要求确定，当高度过高时，应根据水泵传动轴长度产品规格选择较短规格的产品。', '《消防给水及消火栓系统技术规范 GB50974-2014》5.5.7', null, 'B', '资料', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5271', '消火栓系统', '冬季结冰地区消火栓系统选择（A类）', '严寒、寒冷等冬季结冰地区城市隧道及其他构筑物的消火栓系统，应采取防冻措施，并宜采用干式消火栓系统和干式室外消火栓。', '《消防给水及消火栓系统技术规范 GB50974-2014》  5.1.2', null, 'A', '室外', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5272', '消火栓系统', '室外消火栓布置（C类）', '室外消火栓宜沿建筑周围均匀布置，且不宜集中布置在建筑一侧；建筑消防扑救面一侧的室外消火栓数量不宜少于 2 个；', '《消防给水及消火栓系统技术规范 GB50974-2014》7.3.3 C类', null, 'C', '室外', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5273', '消火栓系统', '人防工程、地下工程等建筑的室外消火栓设置（C类）', '人防工程、地下工程等建筑应在出入口附近设置室外消火栓，且距出入口的距离不宜小于 5m， 并不宜大于 40m ；', '《消防给水及消火栓系统技术规范 GB50974-2014》7.3.4  C类', null, 'C', '室外', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5274', '消火栓系统', '停车场的室外消火栓设置（C类）', ' 停车场的室外消火栓宜沿停车场周边设置，且与近一排汽车的距离不宜小于 7 m，距加油站 或油库不宜小于 15 m；', ' 《消防给水及消火栓系统技术规范 GB50974-2014》7.3.5C类', null, 'C', '室外', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5275', '消火栓系统', '甲、乙、丙类液体储罐区和液化烃罐罐区等构筑物的室外消火栓设置（C类）', '甲、乙、丙类液体储罐区和液化烃罐罐区等构筑物的室外消火栓，应设在防火堤或防护墙外，数量应符合设计要求，但距罐壁 15 m 范围内的消火栓，不应计算在该罐可使用的数量内；', '《消防给水及消火栓系统技术规范 GB50974-2014》7.3.6C类', null, 'C', '室外', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5276', '消火栓系统', '工艺装置区等采用高压或临时高压消防给水系统的场所的室外消火栓设置（C类）', '工艺装置区等采用高压或临时高压消防给水系统的场所，其周围应设置室外消火栓，数量应符 合设计要求，且间距不应大于 60 m；当工艺装置区宽度大于 120 m 时，宜在该装置区内的路 边设置室外消火栓；', '《消防给水及消火栓系统技术规范 GB50974-2014》7.3.7C类', null, 'C', '室外', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5277', '消火栓系统', '室外消火栓与路边、建筑外墙的距离及其它要求（C类）', ' 室外消火栓距路边不宜小于0.5 m，并不应大于2.0 m，距建筑外墙或外墙边缘不宜小于 5.0m； 应避免设置在机械易撞击的地点，确有困难时，应采取防撞措施；', '《消防给水及消火栓系统技术规范 GB50974-2014》7.2.6 C类', null, 'C', '室外', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5278', '消火栓系统', '工艺装置区、储罐区、堆场等构筑物采用高压或临时高压消防给水系统是对消火栓的配置要求（C类）', '当工艺装置区、储罐区、堆场等构筑物采用高压或临时高压消防给水系统时，室外消火栓处宜配置消防水带和消防水枪；', ' 《消防给水及消火栓系统技术规范 GB50974-2014》7.3.9 C类', null, 'C', '室外', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5279', '消火栓系统', '室外消防给水引入管（A类）', '室外消防给水引入管当设有倒流防止器时，', ' 《消防给水及消火栓系统技术规范 GB50974-2014》7.3.10 A类', null, 'A', '室外', null, '2019-10-10 20:21:08', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5280', '消火栓系统', '室外消火栓设置要求（A类）', '室外消火栓的设置应符合设计要求。', '《建筑设计防火规范 GB 50016-2014》8．2．1 ', null, 'A', '室外', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5281', '消火栓系统', '室外消防给水管网的其它规定（B类）', '室外消防给水管网应符合下列规定：\n1 室外消防给水采用两路消防供水时应采用环状管网，但当采用一路消防供水时可采用枝状管网；\n2 管道的直径应根据流量、流速和压力要求经计算确定，但不应小于DN100；\n3 消防给水管道应采用阀门分成若干独立段，每段内室外消火栓的数量不宜超过5个；', '《消防给水及消火栓系统技术规范 GB50974-2014》  8．1．4', null, 'B', '室外', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5282', '消火栓系统', '安装位置对管材的选择（B类）', ' 埋地管道宜采用球墨铸铁管、钢丝网骨架塑料复合管和加强防腐的钢管等管材，室内外架空管道应采用热浸锌镀锌钢管等金属管材，并应按下列因素对管道的综合影响选择管材和设计管道：1 系统工作压力；2 覆土深度；3 土壤的性质；4 管道的耐腐蚀能力；5 可能受到土壤、建筑基础、机动车和铁路等其他附加荷载的影响；6 管道穿越伸缩缝和沉降缝。', '《消防给水及消火栓系统技术规范 GB50974-2014》  8．2．4', null, 'B', '室外', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5283', '消火栓系统', '埋地管道根据工作压力对管材的选择（C类）', '埋地管道当系统工作压力不大于1.20MPa时，宜采用球墨铸铁管或钢丝网骨架塑料复合管给水管道；当系统工作压力大于1.20MPa小于1.60MPa时，宜采用钢丝网骨架塑料复合管、加厚钢管和无缝钢管；当系统工作压力大于1.60MPa时，宜采用无缝钢管。钢管连接宜采用沟槽连接件(卡箍)和法兰，当采用沟槽连接件连接时，公称直径小于等于DN250的沟槽式管接头系统工作压力不应大于2.50MPa，公称直径大于或等于DN300的沟槽式管接头系统工作压力不应大于1.60MPa。', '《消防给水及消火栓系统技术规范 GB50974-2014》  8．2．5 ', null, 'C', '室外', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5284', '消火栓系统', '埋地金属管道的管顶覆土要求（B类）', '埋地金属管道的管顶覆土应符合下列规定：\n1 管道最小管顶覆土应按地面荷载、埋深荷载和冰冻线对管道的综合影响确定；\n2 管道最小管顶覆土不应小于0.70m；但当在机动车道下时管道最小管顶覆土应经计算确定，并不宜小于0.90m；\n3 管道最小管顶覆土应至少在冰冻线以下0.30m。', '《消防给水及消火栓系统技术规范 GB50974-2014》 8．2．6 ', null, 'B', '室外', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5285', '消火栓系统', '埋地管道采用钢丝网骨架塑料复合管时应符合的规定（B类）', '埋地管道采用钢丝网骨架塑料复合管时应符合下列规定：\n    1 钢丝网骨架塑料复合管的聚乙烯(PE)原材料不应低于PE80；\n    2 钢丝网骨架塑料复合管的内环向应力不应低于8.0MPa；\n    3 钢丝网骨架塑料复合管的复合层应满足静压稳定性和剥离强度的要求；\n    4 钢丝网骨架塑料复合管及配套管件的熔体质量流动速率(MFR)，应按现行国家标准《热塑性塑料熔体质量流动速率和熔体体积流动速率的测定》GB/T 3682规定的试验方法进行试验时，加工前后MFR变化不应超过±20％；\n    5 管材及连接管件应采用同一品牌产品，连接方式应采用可靠的电熔连接或机械连接；\n    6 管材耐静压强度应符合现行行业标准《埋地聚乙烯给水管道工程技术规程》CJJ 101的有关规定和设计要求；\n    7 钢丝网骨架塑料复合管道最小管顶覆土深度，在人行道下不宜小于0.80m，在轻型车行道下不应小于1.0m，且应在冰冻线下0.30m；在重型汽车道路或铁路、高速公路下应设置保护套管，套管与钢丝网骨架塑料复合管的净距不应小于100mm；\n    8 钢丝网骨架塑料复合管道与热力管道间的距离，应在保证聚乙烯管道表面温度不超过40℃的条件下计算确定，但最小净距不应小于1.50m。', '《消防给水及消火栓系统技术规范 GB50974-2014》  8．2．7 ', null, 'B', '室外', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5286', '消火栓系统', '埋地管道的地基、基础、垫层、回填土压实密度等的要求（B类）', '埋地管道的地基、基础、垫层、回填土压实密度等的要求，应根据刚性管或柔性管管材的性质，结合管道埋设处的具体情况，按现行国家标准《给水排水管道工程施工及验收标准》GB 50268和《给水排水工程管道结构设计规范》GB 50332的有关规定执行。当埋地管直径不小于DN100时，应在管道弯头、三通和堵头等位置设置钢筋混凝土支墩。', '《消防给水及消火栓系统技术规范 GB50974-2014》  8．2．11 ', null, 'B', '室外', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5287', '消火栓系统', '管道防腐措施（B类）', '埋地钢管和铸铁管，应根据土壤和地下水腐蚀性等因素确定管外壁防腐措施；海边、空气潮湿等空气中含有腐蚀性介质的场所的架空管道外壁，应采取相应的防腐措施。', '《消防给水及消火栓系统技术规范 GB50974-2014》  8．2．13 ', null, 'B', '室外', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5288', '消火栓系统', '露天设置的流量开关（B类）', '露天设置的流量开关应有防护措施，流量开关应采用专业的定型产品，不能用普通水流指示器代替； ', '产品规范', null, 'B', '室外', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5289', '消火栓系统', '室外阀门井防冻措施（B类）', '在寒冷、严寒地区，室外阀门井应采取防冻措施。', '《消防给水及消火栓系统技术规范 GB50974-2014》  8．3．6 ', null, 'B', '室外', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5290', '消火栓系统', '室内消火栓布置间距（C类）', '7．4．10 室内消火栓宜按直线距离计算其布置间距，并应符合下列规定：\n1 消火栓按2支消防水枪的2股充实水柱布置的建筑物，消火栓的布置间距不应大于30.0m；\n2 消火栓按1支消防水枪的1股充实水柱布置的建筑物，消火栓的布置间距不应大于50.0m。', '《消防给水及消火栓系统技术规范 GB50974-2014》7.4.10', null, 'C', '楼层', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5291', '消火栓系统', '室内消火栓的充实水柱（B类）', '7．4．6 室内消火栓的布置应满足同一平面有2支消防水枪的2股充实水柱同时达到任何部位的要求，但建筑高度小于或等于24.0m且体积小于或等于5000m3的多层仓库、建筑高度小于或等于54m且每单元设置一部疏散楼梯的住宅，以及本规范表3．5．2中规定可采用1支消防水枪的场所，可采用1支消防水枪的1股充实水柱到达室内任何部位。B类', '《消防给水及消火栓系统技术规范 GB50974-2014》 7.4.6', null, 'B', '楼层', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5292', '消火栓系统', '设备层在内的各层设置消火栓（A类）', '(e) 设置室内消火栓的建筑，包括设备层在内的各层均应设置消火栓；', '《消防给水及消火栓系统技术规范 GB50974-2014》7.4.3', null, 'A', '楼层', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5293', '消火栓系统', '消防电梯前室设置室内消火栓（B类）', '(g) 消防电梯前室应设置室内消火栓；', '《消防给水及消火栓系统技术规范 GB50974-2014》--7.4.5', null, 'B', '楼层', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5294', '消火栓系统', '内消火栓设置位置（B类）', '(h) 室内消火栓应设置在楼梯间及其休息平台和前室、走道等明显易于取用，以及便于火灾扑救的位置；', '《消防给水及消火栓系统技术规范 GB50974-2014》7.4.7  ', null, 'B', '楼层', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5295', '消火栓系统', '汽车库内消火栓的设置（B类）', '（i) 汽车库内消火栓的设置不应影响汽车的通行和车位的设置，并应确保消火栓的开启；', '《消防给水及消火栓系统技术规范 GB50974-2014》7.4.7', null, 'B', '楼层', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5296', '消火栓系统', '消火栓的平面位置（C类）', '(j) 同一楼梯间及其附近不同层设置的消火栓，其平面位置宜相同；', '《消防给水及消火栓系统技术规范 GB50974-2014》7.4.7', null, 'C', '楼层', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5297', '消火栓系统', '室内消火栓管径及位置（B类）', '(b) 应采用DN65室内消火栓，并可与消防软管卷盘或轻便水龙设置在同一箱体内；', '《消防给水及消火栓系统技术规范 GB50974-2014》--7.4.2', null, 'B', '楼层', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5298', '消火栓系统', '室内消火栓栓口的安装高度及出水方向（C类）', '(k) 建筑室内消火栓栓口的安装高度应便于消防水龙带的连接和使用，其距地面高度宜为 1.1 m； 其出水方向应便于消防水带的敷设，并宜与设置消火栓的墙面成 90°角或向下；', '《消防给水及消火栓系统技术规范 GB50974-2014》7.4.8', null, 'C', '楼层', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5299', '消火栓系统', '室内消火栓配置的消防水带、消防软管、轻便水龙（B类）', '(c) 应配置公称直径 65 mm 有内衬里的消防水带，长度不宜超过 25.0 m；消防软管卷盘应配置内径不小于 φ19 mm 的消防软管，其长度宜为 30 m；轻便水龙应配置公称直径 25 mm 有内衬里的消防水带，长度宜为 30 m；', '《消防给水及消火栓系统技术规范 GB50974-2014》7.4.2', null, 'B', '楼层', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5300', '消火栓系统', '室内消火栓配置的当量喷嘴（B类）', '(d) 宜配置当量喷嘴直径 16 mm 或 19 mm 的消防水枪，但当消火栓设计流量为 2.5 L/s 时宜配置当量喷嘴直径11 mm或13 mm的消防水枪；消防软管卷盘和轻便水龙应配置当量喷嘴直径6 mm 的消防水枪； ', '《消防给水及消火栓系统技术规范 GB50974-2014》7.4.2', null, 'B', '楼层', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5301', '消火栓系统', '架空管道根据工作压力对管材的选择（C类）', '架空管道当系统工作压力小于等于1.20MPa时，可采用热浸锌镀锌钢管；当系统工作压力大于1.20MPa时，应采用热浸镀锌加厚钢管或热浸镀锌无缝钢管；当系统工作压力大于1.60MPa时，应采用热浸镀锌无缝钢管。', '《消防给水及消火栓系统技术规范 GB50974-2014》8．2．8 ', null, 'C', '楼层', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5302', '消火栓系统', '竖管管径（B）', '(c) 室内消火栓竖管管径应符合设计要求，且不应小于 DN100； ', '《消防给水及消火栓系统技术规范 GB50974-2014》8.1.5', null, 'B', '楼层', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5303', '消火栓系统', '架空管道的连接（C类）', '架空管道的连接宜采用沟槽连接件(卡箍)、螺纹、法兰、卡压等方式，不宜采用焊接连接。当管径小于或等于DN50时，应采用螺纹和卡压连接，当管径大于DN50时，应采用沟槽连接件连接、法兰连接，当安装空间较小时应采用沟槽连接件连接。', '《消防给水及消火栓系统技术规范 GB50974-2014》 8．2．9 ', null, 'C', '楼层', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5304', '消火栓系统', '架空充水管道（B类）', ' 架空充水管道应设置在环境温度不低于5℃的区域，当环境温度低于5℃时，应采取防冻措施；室外架空管道当温差变化较大时应校核管道系统的膨胀和收缩，并应采取相应的技术措施。', '《消防给水及消火栓系统技术规范 GB50974-2014》8．2．10', null, 'B', '楼层', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5305', '消火栓系统', '消防给水系统标识（C类）', '(h) 消防给水系统的室内外消火栓、阀门等设置位置，应设置永久性固定标识； ', '《消防给水及消火栓系统技术规范 GB50974-2014》8.3.7 ', null, 'C', '楼层', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5306', '消火栓系统', '高位消防水箱稳压泵设置（B类）', '当高位消防水箱不能满足GB 50974  5.2.2.5第 1）款～第 4）款的静压要求时，应设稳压泵。', '《消防给水及消火栓系统技术规范 GB50974-2014》  5.2.2.5', null, 'B', '楼层-屋面', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5307', '消火栓系统', '高位消防水箱功能性要求（B类）', '高位消防水箱的设置位置应高于其所服务的水灭火设施，且低有效水位应满足水灭火设施 不利点处的静水压力，并应满足下列规定：    1) 一类高层公共建筑，不应低于 0.10 MPa，但当建筑高度超过 100 m 时，不应低于 0.15 MPa；   2) 高层住宅、二类高层公共建筑、多层公共建筑，不应低于 0.07 MPa，多层住宅不宜低于 0.07 MPa；    3) 工业建筑不应低于 0.10 MPa，当建筑体积小于 20 000 m3时，不宜低于 0.07 MPa；   4) 自动喷水灭火系统等自动水灭火系统应根据喷头灭火需求压力确定，但小不应小于 0.10 MPa；', '《消防给水及消火栓系统技术规范 GB50974-2014》5.2.2', null, 'B', '楼层-屋面', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5308', '消火栓系统', '高位消防水箱的人孔以及进出水管的阀门（A类）', '当高位消防水箱在屋顶露天设置时，水箱的人孔以及进出水管的阀门等应采取锁具或阀门箱等保护措施；', '《消防给水及消火栓系统技术规范 GB50974-2014》5.2.4.1', null, 'A', '楼层-屋面', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5309', '消火栓系统', '消防水箱防冻隔热（B类）', ' 严寒、寒冷等冬季冰冻地区的消防水箱应设置在消防水箱间内，其他地区宜设置在室内，当必须在屋顶露天设置时，应采取防冻隔热等安全措施；', '《消防给水及消火栓系统技术规范 GB50974-2014》 5.2.4.2 ', null, 'B', '楼层-屋面', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5310', '消火栓系统', '高位消防水箱环境温度或水温（A类）', '高位消防水箱间应通风良好，不应结冰，当必须设置在严寒、寒冷等冬季结冰地区的非采暖房间时，应采取防冻措施，环境温度或水温不应低于 5 ℃；', '《消防给水及消火栓系统技术规范 GB50974-2014》 5.2.5', null, 'A', '楼层-屋面', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5311', '消火栓系统', '高位消防水箱用水与其他用水共用（A类）', '当高位消防水箱用水与其他用水共用时，应采取确保消防用水量不作他用的技术措施；应保证消防水箱的有效容积能被全部利用；高位消防水箱应设置就地水位显示装置，并应在消防控制 中心或值班室等地点设置显示消防水箱水位的装置，同时应有高和低报警水位；高位消防水箱应设置溢流水管和排水设施，并应采用间接排水的规定；', '《消防给水及消火栓系统技术规范 GB50974-2014》 4.3.9', null, 'A', '楼层-屋面', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5312', '消火栓系统', '高位消防水箱的最低有效水位、通气管、呼吸管（A类）', '高位消防水箱的最低有效水位应根据出水管喇叭口和防止旋流器的淹没深度确定，当采用出水管喇叭口时，应符合本规范第5．1．13条第4款的规定；当采用防止旋流器时应根据产品确定，且不应小于150mm的保护高度；\n3 高位消防水箱的通气管、呼吸管等应符合本规范第4．3．10条的规定；', '《消防给水及消火栓系统技术规范 GB50974-2014》 5.2.6（2、3）', null, 'A', '楼层-屋面', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5313', '消火栓系统', '高位消防水箱安装（C类）', '高位消防水箱外壁与建筑本体结构墙面或其他池壁之间的净距，无管道的侧面，净距不宜小于 0.7 m；安装有管道的侧面，净距不宜小于 1.0 m，且管道外壁与建筑本体墙面之间的通道宽度 不宜小于 0.6 m，设有人孔的水箱顶，其顶面与其上面的建筑物本体板底的净空不应小于 0.8 m；', '《消防给水及消火栓系统技术规范 GB50974-2014》5.2.6.4 ', null, 'C', '楼层-屋面', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5314', '消火栓系统', '高位消防水箱进水管冲水要求（B类）', '进水管的管径应满足消防水箱8h充满水的要求，', '《消防给水及消火栓系统技术规范 GB50974-2014》5.2.6.5 ', null, 'B', '楼层-屋面', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5315', '消火栓系统', '高位消防水箱进水管接入（B类）', ' 进水管应在溢流水位以上接入，进水管口的最低点高出溢流边缘的高度应等于进水管管径，但最小不应小于100mm，最大不应大于150mm；', '《消防给水及消火栓系统技术规范 GB50974-2014》5.2.6.6', null, 'B', '楼层-屋面', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5316', '消火栓系统', '进水管淹没出流（B类）', '当进水管为淹没出流时，应在进水管上设置防止倒流的措施或在管道上设置虹吸破坏孔和真空破坏器，虹吸破坏孔的孔径不宜小于管径的1/5，且不应小于25mm。但当采用生活给水系统补水时，进水管不应淹没出流；', '《消防给水及消火栓系统技术规范 GB50974-2014》 5.2.6.7', null, 'B', '楼层-屋面', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5317', '消火栓系统', '进水管、溢流管、喇叭口、出水管管径（B类）', '进水管管径应满足设计要求，但管径不应小于 DN32，进水管宜设置液位阀或浮球阀；溢流管的直径不应小于进水管直径的 2 倍，且不应小于 DN100，喇叭口直径不应小于溢流管直径的 （1.5～2.5）倍；高位消防水箱出水管管径应满足消防给水设计流量的出水要求，且不应小于 DN100；', '《消防给水及消火栓系统技术规范 GB50974-2014》 5.2.6   (5 8 9 ）', null, 'B', '楼层-屋面', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5318', '消火栓系统', '高位消防水箱出水管位置及止回阀设置（B类）', ' 高位消防水箱出水管应位于高位消防水箱低水位以下，并应设置防止消防用水进入高位消防水箱的止回阀；', '《消防给水及消火栓系统技术规范 GB50974-2014》5.2.6.10', null, 'B', '楼层-屋面', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5319', '消火栓系统', '高位消防水箱的进、出水管阀门（B类）', '高位消防水箱的进、出水管应设置带有指示启闭装置的阀门。', '《消防给水及消火栓系统技术规范 GB50974-2014》 5.2.6.11', null, 'B', '楼层-屋面', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5320', '消火栓系统', '环状给水管网（B类）', '向两栋或两座及以上建筑供水，向两种及以上水灭火系统供水，采用设有高位消防水箱的临时高压消防给水系统时以及向两个及以上报警阀控制的自动水灭火系统供水应采用环状给水管网；', '《消防给水及消火栓系统技术规范 GB50974-2014》8.1.2', null, 'B', '楼层-屋面', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5321', '消火栓系统', '枝状给水管网（B类）', '室内消火栓系统管网应布置成环状，当室外消火栓设计流量不大于20L/s，且室内消火栓不超过10个时，除 (a)条外，可布置成枝状；', '《消防给水及消火栓系统技术规范 GB50974-2014》8.1.5', null, 'B', '楼层-屋面', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5322', '消火栓系统', '消防给水系统管道的自动排气阀（C类）', '消防给水系统管道的高点处宜设置自动排气阀； ', '《消防给水及消火栓系统技术规范 GB50974-2014》8.3.2', null, 'C', '楼层-屋面', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5323', '消火栓系统', '消防给水系统的阀门选择（B类）', '消防给水系统的阀门选择应符合下列规定：\n1.本项目不涉及2 室内架空管道的阀门宜采用蝶阀、明杆闸阀或带启闭刻度的暗杆闸阀等；3 室外架空管道宜采用带启闭刻度的暗杆闸阀或耐腐蚀的明杆闸阀；4 室内架空管道的阀门应采用球墨铸铁或不锈钢阀门，室外架空管道的阀门应采用球墨铸铁阀门或不锈钢阀门。', '《消防给水及消火栓系统技术规范 GB50974-2014》8．3．1', null, 'B', '楼层-屋面', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5324', '消火栓系统', '竖管数量及竖管阀门（B）', '室内消火栓竖管应保证检修管道时关闭停用的竖管不超过1根，当竖管超过4根时，可关闭不相邻的2根；每根竖管与供水横干管相接处应设置阀门；', '《消防给水及消火栓系统技术规范 GB50974-2014》8.1.6', null, 'B', '楼层-屋面', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5325', '消火栓系统', '带有压力表的试验消火栓（C类）', '设有室内消火栓的建筑应设置带有压力表的试验消火栓，其设置位置应符合：多层和高层建筑应在其屋顶设置，严寒、寒冷等冬季结冰地区可设置在顶层出口处或水箱间内等便于操作和防冻的位置；单层建筑宜设置在水力不利处，且应靠近出入口； ', '《消防给水及消火栓系统技术规范 GB50974-2014》7.4.9', null, 'C', '楼层-屋面', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5326', '消火栓系统', '稳压泵的设计压力与系统不利点处水灭火设施静水压力的关系（B类）', '稳压泵的设计压力应保持系统不利点处水灭火设施在准工作状态时的静水压力应大于 0.15 MPa。', '《消防给水及消火栓系统技术规范 GB50974-2014》5.3.3', null, 'B', '楼层-屋面', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5327', '消火栓系统', '稳压泵的设计压力与系统自动启动泵压力值关系（B类）', '稳压泵的设计压力应保持系统自动启泵压力设置点处的压力在准工作状态时大于系统设置自动启泵压力值，且增加值宜为（0.07～0.10）MPa； B类', '《消防给水及消火栓系统技术规范 GB50974-2014》 5.3.3.2', null, 'B', '楼层-屋面', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5328', '消火栓系统', '消火栓联动控制（A类）', '消火栓联动控制方式，应由消火栓系统出水干管上设置的低压压力开关、高位消防水箱出水管上设置的流量开关或报警阀压力开关（干式系统用）等信号作为触发信号，直接连锁控制启动消火栓泵，联动控制不应受消防联动控制器处于自动或手动状态影响；\n', '《火灾自动报警系统设计规范 GB50116-2013》4.3.1', null, 'A', '楼层-屋面', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5329', '消火栓系统', '消火栓按钮（A类）', '当设置消火栓按钮时，消火栓按钮的动作信号应作为报警信号及启动消火栓泵的联动触发信号，由消防联动控制器联动控制消火栓泵的启动。\n', '《火灾自动报警系统设计规范 GB50116-2013》4.3.1', null, 'A', '楼层-屋面', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5330', '消火栓系统', '手动控制方式的线路连接（A类）', '手动控制方式，应将消火栓泵控制箱（柜）的启动、停止按钮用专用线路直接连接至设置在消 防控制室内的消防联动控制器的手动控制盘，直接手动控制消火栓泵的启动、停止；\n', '《火灾自动报警系统设计规范 GB50116-2013》4.3.2', null, 'A', '楼层-屋面', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5331', '消火栓系统', '消火栓泵的动作信号（A类）', '消火栓泵的动作信号应反馈至消防联动控制器； ', '《火灾自动报警系统设计规范 GB50116-2013》4.3.3', null, 'A', '楼层-屋面', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5332', '消火栓系统', '消火栓栓口动压力（B类）', '消火栓栓口动压力不应大于 0.50 MPa；', '《消防给水及消火栓系统技术规范 GB50974-2014》7.4.12', null, 'B', '楼层-屋面', null, '2019-10-10 20:21:09', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5333', '消火栓系统', '消火栓栓口动压及消防水枪充实水柱（B类）', '高层建筑、厂房、库房和室内净空高度超过 8 m 的民用建筑等场所，消火栓栓口动压不应小于 0.35 MPa，且消防水枪充实水柱应是 13 m；其他场所，消火栓栓口动压不应小于 0.25 MPa， 且消防水枪充实水柱应为 10 m；', '《消防给水及消火栓系统技术规范 GB50974-2014》7.4.12', null, 'B', '楼层-屋面', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5334', '消火栓系统', '消火栓栓口的减压装置（B类）', '当消火栓栓口动压力大于 0.70 MPa 时应设置减压装置；', '《消防给水及消火栓系统技术规范 GB50974-2014》7.4.12', null, 'B', '楼层-屋面', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5335', '消火栓系统', '超高层分区供水系统的消防水泵联动控制（B类）', '超高层分区供水系统的消防水泵联动控制应符合 16D303-3 的相关要求。\n国标无B类', 'GB-16D303-3', null, 'B', '楼层-屋面', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5336', '消火栓系统', '减压阀的设置（B类）', '减压阀的设置应符合设计要求及 4.3.2.1 d)条的要求。', '《消防给水及消火栓系统技术规范 GB50974-2014》', null, 'B', '楼层-底层', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5337', '消火栓系统', '减压阀设置(B 类项)', '配水管道的工作压力不应大于 1.20MPa。', '《自动喷水灭火系统施工及验收规范 GB50261-2017》8.0.1', null, 'B', '楼层-底层', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5338', '消火栓系统', '减压阀的设置（B类）', '减压阀的设置应符合下列规定：\n1 减压阀应设置在报警阀组入口前，当连接两个及以上报警阀组时，应设置备用减压阀；\n2 减压阀的进口处应设置过滤器，过滤器的孔网直径不宜小于4目/cm2～5目/cm2，过流面积不应小于管道截面积的4倍；\n3 过滤器和减压阀前后应设压力表，压力表的表盘直径不应小于100mm，最大量程宜为设计压力的2倍；\n4 过滤器前和减压阀后应设置控制阀门；\n5 减压阀后应设置压力试验排水阀；\n6 减压阀应设置流量检测测试接口或流量计；\n7 垂直安装的减压阀，水流方向宜向下；\n8 比例式减压阀宜垂直安装，可调式减压阀宜水平安装；\n9 减压阀和控制阀门宜有保护或锁定调节配件的装置；\n10 接减压阀的管段不应有气堵、气阻。', '《消防给水及消火栓系统技术规范 GB50974-2014》8．3．4', null, 'B', '楼层-底层', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5339', '消火栓系统', '减压阀处管道直径及水池的出水、排水和水位（A类）', '减压阀处的压力试验排水管道直径应根据减压阀流量确定，但不应小于 DN100；消防水池、 高位消防水池的出水、排水和水位应符合下列规定：\n1) 消防水池的出水管应保证消防水池的有效容积能被全部利用； \n2) 应设置就地水位显示装置，并应在消防控制室或值班室等地点设置显示消防水池水位的装 置，同时应有高和低报警水位； \n3) 应设置溢流水管和排水设施，并应采用间接排水。', '《消防给水及消火栓系统技术规范 GB50974-2014》9.3.1《消防给水及消火栓系统技术规范 GB50974-2014》4.3.9', null, 'A', '楼层-底层', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5340', '消火栓系统', '当市政给水管网设有市政消火栓时对管网的要求（B类）', '当市政给水管网设有市政消火栓时，应符合下列规定：\n1 设有市政消火栓的市政给水管网宜为环状管网，但当城镇人口小于2.5万人时，可为枝状管网；\n2 接市政消火栓的环状给水管网的管径不应小于DN150，枝状管网的管径不宜小于DN200。当城镇人口小于2.5万人时，接市政消火栓的给水管网的管径可适当减少，环状管网时不应小于DN100，枝状管网时不宜小于DN150；\n3 工业园区、商务区和居住区等区域采用两路消防供水，当其中一条引入管发生故障时，其余引入管在保证满足70％生产生活给水的最大小时设计流量条件下，应仍能满足本规范规定的消防给水设计流量。', '\n《消防给水及消火栓系统技术规范 GB50974-2014》8．1．1 ', null, 'B', '资料', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5341', '消火栓系统', '向室外、室内环状消防给水管网供水的输水干管（B类）', '向室外、室内环状消防给水管网供水的输水干管不应少于两条，当其中一条发生故障时，其余的输水干管应仍能满足消防给水设计流量。', '\n《消防给水及消火栓系统技术规范 GB50974-2014》8．1．3', null, 'B', '资料', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5342', '消火栓系统', '产品压力等级（B类）', '消防给水系统中采用的设备、器材、管材管件、阀门和配件等系统组件的产品工作压力等级，应大于消防给水系统的系统工作压力，且应保证系统在可能最大运行压力时安全可靠。', '\n《消防给水及消火栓系统技术规范 GB50974-2014》8．2．1 ', null, 'B', '资料', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5343', '消火栓系统', '埋地管道根据工作压力对管材的选择（C类）', '埋地管道当系统工作压力不大于1.20MPa时，宜采用球墨铸铁管或钢丝网骨架塑料复合管给水管道；当系统工作压力大于1.20MPa小于1.60MPa时，宜采用钢丝网骨架塑料复合管、加厚钢管和无缝钢管；当系统工作压力大于1.60MPa时，宜采用无缝钢管。钢管连接宜采用沟槽连接件(卡箍)和法兰，当采用沟槽连接件连接时，公称直径小于等于DN250的沟槽式管接头系统工作压力不应大于2.50MPa，公称直径大于或等于DN300的沟槽式管接头系统工作压力不应大于1.60MPa。', '\n《消防给水及消火栓系统技术规范 GB50974-2014》8．2．5', null, 'C', '资料', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5344', '消火栓系统', '埋地金属管道的管顶覆土要求（B类）', '埋地金属管道的管顶覆土应符合下列规定：\n1 管道最小管顶覆土应按地面荷载、埋深荷载和冰冻线对管道的综合影响确定；\n2 管道最小管顶覆土不应小于0.70m；但当在机动车道下时管道最小管顶覆土应经计算确定，并不宜小于0.90m；\n3 管道最小管顶覆土应至少在冰冻线以下0.30m。', '\n《消防给水及消火栓系统技术规范 GB50974-2014》8．2．6', null, 'B', '资料', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5345', '消火栓系统', '埋地管道采用钢丝网骨架塑料复合管时应符合的规定（B类）', '埋地管道采用钢丝网骨架塑料复合管时应符合下列规定：\n    1 钢丝网骨架塑料复合管的聚乙烯(PE)原材料不应低于PE80；\n    2 钢丝网骨架塑料复合管的内环向应力不应低于8.0MPa；\n    3 钢丝网骨架塑料复合管的复合层应满足静压稳定性和剥离强度的要求；\n    4 钢丝网骨架塑料复合管及配套管件的熔体质量流动速率(MFR)，应按现行国家标准《热塑性塑料熔体质量流动速率和熔体体积流动速率的测定》GB/T 3682规定的试验方法进行试验时，加工前后MFR变化不应超过±20％；\n    5 管材及连接管件应采用同一品牌产品，连接方式应采用可靠的电熔连接或机械连接；\n    6 管材耐静压强度应符合现行行业标准《埋地聚乙烯给水管道工程技术规程》CJJ 101的有关规定和设计要求；\n    7 钢丝网骨架塑料复合管道最小管顶覆土深度，在人行道下不宜小于0.80m，在轻型车行道下不应小于1.0m，且应在冰冻线下0.30m；在重型汽车道路或铁路、高速公路下应设置保护套管，套管与钢丝网骨架塑料复合管的净距不应小于100mm；\n    8 钢丝网骨架塑料复合管道与热力管道间的距离，应在保证聚乙烯管道表面温度不超过40℃的条件下计算确定，但最小净距不应小于1.50m。', '\n《消防给水及消火栓系统技术规范 GB50974-2014》 8．2．7', null, 'B', '资料', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5346', '消火栓系统', '埋地管道的地基、基础、垫层、回填土压实密度等的要求（B类）', '埋地管道的地基、基础、垫层、回填土压实密度等的要求，应根据刚性管或柔性管管材的性质，结合管道埋设处的具体情况，按现行国家标准《给水排水管道工程施工及验收标准》GB 50268和《给水排水工程管道结构设计规范》GB 50332的有关规定执行。当埋地管直径不小于DN100时，应在管道弯头、三通和堵头等位置设置钢筋混凝土支墩。', '\n《消防给水及消火栓系统技术规范 GB50974-2014》8．2．11', null, 'B', '资料', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5347', '消火栓系统', '消防给水管道穿越建筑基础（B类）', '消防给水管道不宜穿越建筑基础，当必须穿越时，应采取防护套管等保护措施。', '\n《消防给水及消火栓系统技术规范 GB50974-2014》8．2．12', null, 'B', '资料', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5348', '消火栓系统', '管道防腐措施（B类）', '埋地钢管和铸铁管，应根据土壤和地下水腐蚀性等因素确定管外壁防腐措施；海边、空气潮湿等空气中含有腐蚀性介质的场所的架空管道外壁，应采取相应的防腐措施。', '\n《消防给水及消火栓系统技术规范 GB50974-2014》8．2．13', null, 'B', '资料', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5349', '消火栓系统', '消防给水系统的阀门选择（B类）', '消防给水系统的阀门选择应符合下列规定：\n1 埋地管道的阀门宜采用带启闭刻度的暗杆闸阀，当设置在阀门井内时可采用耐腐蚀的明杆闸阀；\n4 埋地管道的阀门应采用球墨铸铁阀门。', '\n《消防给水及消火栓系统技术规范 GB50974-2014》8．3．1', null, 'B', '资料', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5350', '消火栓系统', '离心泵（C类）', '稳压泵宜采用离心泵，并宜符合下列规定：\n1 宜采用单吸单级或单吸多级离心泵；\n2 泵外壳和叶轮等主要部件的材质宜采用不锈钢。', '《消防给水及消火栓系统技术规范 GB50974-2014》5．3．1', null, 'C', '稳压泵', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5351', '消火栓系统', '设置稳压泵的临时高压消防给水系统（B类）', '设置稳压泵的临时高压消防给水系统应设置防止稳压泵频繁启停的技术措施，当采用气压水罐时其有效储水容积不宜小于 150 L；', '《消防给水及消火栓系统技术规范 GB50974-2014》5.3.4', null, 'B', '稳压泵', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5352', '消火栓系统', '稳压泵吸水管、出水管（B类）', '稳压泵吸水管应设置明杆闸阀，出水管应设置消声止回阀和明杆闸阀；', '《消防给水及消火栓系统技术规范 GB50974-2014》5.3.5', null, 'B', '稳压泵', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5353', '消火栓系统', '备用泵（B类）', '稳压泵应设置备用泵；', '《消防给水及消火栓系统技术规范 GB50974-2014》5.3.6', null, 'B', '稳压泵', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5354', '防火卷帘、防火门、防火窗', '防火卷帘的宽度（B类）', 'b) 除中庭外，当防火分隔部位的宽度不大于 30 m 时，防火卷帘的宽度不应大于 10 m；当防火分隔部位的宽度大于 30 m 时，防火卷帘的宽度不应大于该部位宽度的 1/3，且不应大于 20 m； ', '《建筑设计防火规范 GB 50016-2014》 6.5.3 ', null, 'B', '楼层', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5355', '防火卷帘、防火门、防火窗', '防火卷帘外观及材料要求（C类）', 'd) 帘板金属零部件表面不应有裂纹、压坑及明显的凹凸等缺陷；无机纤维复合帘面不应有撕裂、缺角、挖补等缺陷；', '《防火卷帘、防火门、防火窗施工及验收规范 GB50877-2014》 5.2.1 ', null, 'C', '楼层', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5356', '防火卷帘、防火门、防火窗', '帘板嵌入导轨的深度（C类）', 'e) 帘板嵌入导轨的深度应符合下列规定：\n 1) 导轨间距离小于 3.0 m 时，每端嵌入深度应大于 45 mm； \n2) 导轨间距离大于等于 3.0 m 且小于 5.0 m 时，每端嵌入深度应大于 50 mm； \n3) 导轨间距离大于等于 5.0 m 时，每端嵌入深度应大于 60 mm； 4) 导轨间距离每增加 1 000 mm 时，每端嵌入深度应增加 10 mm。 GB 50877 5.2.2 C类', ' 《防火卷帘、防火门、防火窗施工及验收规范 GB50877-2014》 5.2.2 ', null, 'C', '楼层', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5357', '防火卷帘、防火门、防火窗', '座板（C类）', 'f) 座板与地面应平行、接触应均匀；', '《防火卷帘 GB14102-2005》 6.3.6.1', null, 'C', '楼层', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5358', '防火卷帘、防火门、防火窗', '设在疏散走道上的防火卷帘手动控制方式（B类）', 'g) 设在疏散走道上的防火卷帘手动控制方式，应由防火卷帘两侧设置的手动控制按钮控制防火卷帘升降； B类', '产品规范', null, 'B', '楼层', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5359', '防火卷帘、防火门、防火窗', ' 防火卷帘的运行性能（C类）', 'h) 防火卷帘的运行性能： 1) 帘面在导轨内运行应平稳，不应有脱轨和明显的倾斜；双帘面卷帘的两个帘面应同时升降， 两个帘面之间的高度差不应大于 50 mm；', '《防火卷帘、防火门、防火窗施工及验收规范 GB50877-2014》 6.2.3 ', null, 'C', '楼层', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5360', '防火卷帘、防火门、防火窗', '防火卷帘启、闭运行的平均噪声（C类）', 'h) 2) 防火卷帘启、闭运行的平均噪声不应大于 85 dB。\n', '《防火卷帘、防火门、防火窗施工及验收规范 GB50877-2014》 6.2.3', null, 'C', '楼层', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5361', '防火卷帘、防火门、防火窗', '自重自动关闭功能及启动卷帘自重下降的臂力（C类）', 'c) 防火卷帘应具有火灾时靠自重自动关闭功能。卷门机应具有电动启闭和依靠卷帘门自重恒速下降的功能，启动卷帘自重下降的臂力不应大于 70 N；\n', '《建筑设计防火规范 GB 50016-2014》6.5.3', null, 'C', '楼层', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5362', '防火卷帘、防火门、防火窗', '防火卷帘的控制（A类）', 'i) 防火卷帘的控制： 1) 防火卷帘下降至距离楼板面 1.8 m 处、下降到楼板面的动作信号和防火卷帘控制器直接连 接的感烟、感温火灾探测器的报警信号，应反馈至消防联动控制器； 2) 安装在疏散通道上的防火卷帘，应在防火卷帘两侧设置启闭装置，并应具有自动、手动和 机械控制的功能；安装在非疏散通道上的防火卷帘，除防火卷帘两侧设置启闭装置，还应 能在消防控制室内的消防联动控制器上手动控制防火卷帘的降落； 3) 疏散通道上的防火卷帘联动控制时，防火分区内任两只独立的感烟火灾探测器或任一只专 门用于联动防火卷帘的感烟火灾探测器的报警信号联动控制防火卷帘下降至距楼板 1.8 m 处；任一只专门用于联动防火卷帘的感温火灾探测器的报警信号联动控制防火卷帘下降到 楼板面；在卷帘的任一距离卷帘纵深（0.5～5）m 内应设置不少于 2 只专门用于联动防火 卷帘的感温火灾探测器（地下车库车辆通道上设置的防火卷帘也按照此要求）；  4) 非疏散通道上的防火卷帘，联动控制时，由防火卷帘所在的防火分区内任两只独立的火灾 探测器的报警信号，作为防火卷帘下降的联动触发信号，由防火卷帘控制器联动防火卷帘 直接下降到楼板面。 \n', '《防火卷帘、防火门、防火窗施工及验收规范 GB50877-2014》 5.2.6  《火灾自动报警系统设计规范 GB50116-2013》4.6.3', null, 'A', '楼层', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5363', '防火卷帘、防火门、防火窗', '防火门开启方向（A类）', ' 除特殊情况外，防火门应向疏散方向开启，防火门在关闭后应从任何一侧手动开启；', '《防火卷帘、防火门、防火窗施工及验收规范 GB50877-2014》5.3.1', null, 'A', '楼梯间', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5364', '防火卷帘、防火门、防火窗', '防火门外观（C类）', '门的门框、门扇及配件表面应平整、光洁、并应无明显凹痕或机械损伤；', '《防火门 GB12955-2008》5.4.2', null, 'C', '楼梯间', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5365', '防火卷帘、防火门、防火窗', '防火门安装（C类）', '防火门门框与门扇、门扇与门扇的缝隙处嵌装的防火封堵件应牢固完好； ', '《防火卷帘、防火门、防火窗施工及验收规范 GB50877-2014》5.3.6 ', null, 'C', '楼梯间', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5366', '防火卷帘、防火门、防火窗', '门扇与门框的、搭接尺寸、配合活动间隙（C类）', '门扇与门框的、搭接尺寸、配合活动间隙应符合下列规定： 1) 门扇与门框搭接尺寸不应小于 12 mm； 2) 门扇与门框的配和活动间隙不应大于 3 mm； 3) 双扇、多扇们的门扇之间缝隙不应大于 3 mm； 4) 门扇与门框或地面的活动间隙不应大于 9 mm； 5) 门扇与门框贴合面间隙、门扇与门框有合页一侧、有锁一侧及上框的贴合面间隙，均不应 大于 3 mm。 ', '《防火门 GB12955-2008》5.8.2', null, 'C', '楼梯间', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5367', '防火卷帘、防火门、防火窗', '常闭防火门标识（C类）', '常闭防火门应在其明显的位置设置“保持防火门关闭”等提示标识； ', '《建筑设计防火规范 GB 50016-2014》6.5.1   ', null, 'C', '楼梯间', null, '2019-10-10 20:21:10', null, '昆明监狱');
