INSERT INTO `t_question_company` VALUES ('5373', '防火卷帘、防火门、防火窗', '门磁开关的安装（C类）', '门磁开关的安装不得破坏门扇与门框之间的密闭性，常闭门开启角度大于 5°门磁开关应能可靠反馈开门信号给防火门监控器；', '产品规范', null, 'C', '楼梯间', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5374', '防火卷帘、防火门、防火窗', '监控器、监控分机及电源箱的备电池的负载（B类）', '监控器、监控分机及电源箱的备电池容量应能满足全部负载正常工作状态下可靠工作3 h要求；', '《防火门监控器 GB 29364-2012》4.3.3.1 b ', null, 'B', '楼梯间', null, '2019-10-10 20:21:10', null, '昆明监狱');
