INSERT INTO `t_question_company` VALUES ('5377', '防火卷帘、防火门、防火窗', '防火门监控系统接入（C类）', '防火门监控系统应为独立的子系统，电磁释放器、定位与释放装置及门磁开关等不应接入火灾报警总线', '产品规范', null, 'C', '楼梯间', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5378', '防火卷帘、防火门、防火窗', '防火门监控器设置位置（C类）', '防火门监控器应设置在消防控制室，在无消防控制室的场所，监控器应设置在有人值班的场所。 监控分机及电源箱可设置在电气竖井或楼层配电间等处', '产品规范', null, 'C', '楼梯间', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5379', '防火卷帘、防火门、防火窗', '手动控制按钮（C类）', '疏散通道上防火门电动开门器或门禁电磁锁的手动控制按钮应设置在防火门内侧墙面上，距门不宜超过 0.5 m，底边距地面高度宜为（0.9～1.3）m', '产品规范', null, 'C', '楼梯间', null, '2019-10-10 20:21:10', null, '昆明监狱');
