INSERT INTO `t_question_company` VALUES ('5381', '防火卷帘、防火门、防火窗', '手动操作防火门监控器（B类）', '手动操作防火门监控器，应能直接控制与其连接的每个释放器或定位与释放装置的工作状态， 并点亮其启动总指示灯、显示门磁开关或定位与释放装置的反馈信号；', '产品规范', null, 'B', '楼梯间', null, '2019-10-10 20:21:10', null, '昆明监狱');
