INSERT INTO `t_question_company` VALUES ('5383', '防火卷帘、防火门、防火窗', '输入设定的联动控制信号（B类）', '输入设定的联动控制信号，防火门监控器应在 30 s 内输出启动信号，点亮启动总指示灯； ', '《防火门监控器 GB 29364-2012》4.3.1.7', null, 'B', '楼梯间', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5384', '防火卷帘、防火门、防火窗', '监控器信号接收时间及总指示灯（B类）', '监控器应在定位与释放装置、释放器或门磁开关动作后 10 s 内收到反馈信号；发出启动信号 后 10 s 内未收到要求的反馈信号时，应使启动总指示灯闪亮，并显示相应的部位,保持至监控 器收到反馈信号；', '《防火门监控器 GB 29364-2012》4.3.1.8', null, 'B', '楼梯间', null, '2019-10-10 20:21:10', null, '昆明监狱');
INSERT INTO `t_question_company` VALUES ('5385', '防火卷帘、防火门、防火窗', '常闭防火门（B类）', '使任一常闭防火门处于半关闭状态，防火门监控器应在 100 s 内发出故障报警信号。故障声信 号每分钟至少提示 1 次，持续时间应为（1～3）s；', '《防火门监控器 GB 29364-2012》4.3.1.9', null, 'B', '楼梯间', null, '2019-10-10 20:21:10', null, '昆明监狱');
