INSERT INTO `t_question_company` VALUES ('5393', '防火卷帘、防火门、防火窗', '插入锁扣后自动复位功能（C类）', '防火门门磁释放器在插入锁扣后应能自动复位，释放时链条锁扣应能可靠自动脱落； ', '防火门门磁释放器产品规范', null, 'C', '楼梯间', null, '2019-10-10 20:21:10', null, '昆明监狱');
