INSERT INTO `t_question_company` VALUES ('5401', '防火卷帘、防火门、防火窗', '钢质防火窗窗框（C类）', '钢质防火窗窗框内应填充水泥砂浆。窗框与墙体应用预埋钢件或膨胀螺栓等连接固定，其固定 点间距不宜大于 600 mm；', '《防火卷帘、防火门、防火窗施工及验收规范 GB50877-2014》5.3.8 ', null, 'C', '楼梯间', null, '2019-10-10 20:21:10', null, '昆明监狱');
